import { ADD_TO_CART } from "../constants/cartConstant";

export const cartReducer = (state = {cartItems : []}, action) =>{
    switch (action.type) {
        case ADD_TO_CART:

                const item = action.payload

                const isItemExist = state.cartItems.find(i=> i.product.product === item.product.product)

                if(isItemExist){
                    return {
                        ...state,
                        cartItems: state.cartItems.map(i => i.product.product ===isItemExist.product.product ? item: i )
                    }

                }else{
                    return{
                        ...state,
                        cartItems:[...state.cartItems, item]
                    }
                }




        default:
            return state;
    }
}
